"use client";

import React from "react";
import { motion, AnimatePresence } from "framer-motion";
import { MaskedTextReveal } from "../ui/MaskedTextReveal";

const FooterLink = ({
  href,
  children,
}: {
  href: string;
  children: React.ReactNode;
}) => (
  <a
    href={href}
    className="text-[#888] hover:text-[#990000] transition-colors duration-300 text-sm md:text-base mb-2 block w-fit"
  >
    {children}
  </a>
);

const SocialButton = ({
  href,
  icon,
  label,
}: {
  href: string;
  icon: React.ReactNode;
  label: string;
}) => {
  const [isHovered, setIsHovered] = React.useState(false);

  return (
    <a
      href={href}
      target="_blank"
      rel="noopener noreferrer"
      className="relative w-10 h-10 rounded-full bg-[#1a1a1a] flex items-center justify-center hover:bg-[#990000] hover:text-white transition-all duration-300 text-white group"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {icon}
      <AnimatePresence>
        {isHovered && (
          <motion.div
            initial={{ opacity: 0, y: 5, scale: 0.9, filter: "blur(4px)" }}
            animate={{ opacity: 1, y: -45, scale: 1, filter: "blur(0px)" }}
            exit={{ opacity: 0, y: 5, scale: 0.9, filter: "blur(4px)" }}
            transition={{ duration: 0.2, ease: "easeInOut" }}
            className="absolute left-1/2 -translate-x-1/2 bg-[#1a1a1a]/80 backdrop-blur-md text-white text-[11px] font-medium px-3 py-1.5 rounded-lg border border-[#333] whitespace-nowrap z-50 pointer-events-none shadow-xl"
          >
            {label}
          </motion.div>
        )}
      </AnimatePresence>
    </a>
  );
};

